Rules.InvariantScript:
First(
    Sort(
        ForAll(
            cmp_GCThemeMenu.MenuItems As item,
            {
                ID: item.Id,
                Name: item.Name,
                Len: Len(item.Name)
            }
        ),
    Len,
    Descending
    )
).Name

// If the table row has a submenu by default open the submenu
If(CountRows(ThisItem.SubMenu) > 0,
    Select(imgSubMenu_GCTM),
    Set(gblSelectedNavItemName, ThisItem.Name);
    Set(locShowSubMenu, false);
    Navigate(ThisItem.Screen, ScreenTransition.None);
    cmp_GCThemeMenu.onSelectNavigationLink()
)

/*If(
    locCurrentMenu = ThisItem.Id && locShowSubMenu,
    
    Set(locShowSubMenu, true);
    Set(locShowSubMenu, false),
    
    Set(locCurrentMenu, ThisItem.Id);
    Set(locShowSubMenu, false);
    Set(locShowSubMenu, true);
)*/
If(CountRows(ThisItem.SubMenu) > 0,
    Set(locCurrentMenu, ThisItem.Id);
    If(locShowSubMenu,
        Set(locShowSubMenu, false),
        Set(locShowSubMenu, true)
    ),
    Set(locShowSubMenu, false);
)

First(
    Sort(
        ForAll(
            LookUp(cmp_GCThemeMenu.MenuItems, Id = locCurrentMenu).SubMenu As item,
            {
                ID: item.Value.SubMenuId,
                Name: item.Value.Name,
                Len: Len(item.Value.Name)
            }
        ),
    Len,
    Descending
    )
).Name

If(!IsBlank(ThisItem.Value.Link), 
    Set(locShowSubMenu, false);
    Launch(ThisItem.Value.Link), 
    
    If(!IsBlank(ThisItem.Value.Screen), 
        Set(gblSelectedNavItemName, ThisItem.Value.Name);
        cmp_GCThemeMenu.onSelectNavigationLink();
        Set(locShowSubMenu, false);
        Navigate(ThisItem.Value.Screen),
        false
    )
)

